using System.Numerics;
using Nethereum.ABI.FunctionEncoding.Attributes;
using Nethereum.Contracts;

namespace Crypto.SmartContracts.Ethereum.ContractDefinition;

public class TransferAgreementContractDeployment : TransferAgreementContractDeploymentBase
{
    public TransferAgreementContractDeployment() : base(BYTECODE) { }
    public TransferAgreementContractDeployment(string byteCode) : base(byteCode) { }
}

public class TransferAgreementContractDeploymentBase : ContractDeploymentMessage
{
    public static string BYTECODE = "";
    public TransferAgreementContractDeploymentBase() : base(BYTECODE) { }
    public TransferAgreementContractDeploymentBase(string byteCode) : base(byteCode) { }

}

public class DenyTransferFunction : DenyTransferFunctionBase { }

[Function("deny_transfer")]
public class DenyTransferFunctionBase : FunctionMessage
{
    [Parameter("string", "slot_id", 1)]
    public string SlotId { get; set; }
}

public class StoreInBlockFunction : StoreInBlockFunctionBase { }

[Function("store_in_block")]
public class StoreInBlockFunctionBase : FunctionMessage
{
    [Parameter("string", "slot_id", 1)]
    public string SlotId { get; set; }
    [Parameter("address", "recipient", 2)]
    public string Recipient { get; set; }
}

public class TransferToRecipientFunction : TransferToRecipientFunctionBase { }

[Function("transfer_to_recipient")]
public class TransferToRecipientFunctionBase : FunctionMessage
{
    [Parameter("string", "slot_id", 1)]
    public string SlotId { get; set; }
}

public class TransferEventDTO : TransferEventDTOBase { }

[Event("Transfer")]
public class TransferEventDTOBase : IEventDTO
{
    [Parameter("address", "sender", 1, true )]
    public string Sender { get; set; }
    [Parameter("address", "recipient", 2, true )]
    public string Recipient { get; set; }
    [Parameter("string", "slot_id", 3, true )]
    public string SlotId { get; set; }
    [Parameter("uint256", "money", 4, false )]
    public BigInteger Money { get; set; }
}